const carousels=document.querySelectorAll(".carousel-wrapper");carousels.forEach((e=>{const r=e?.querySelector(".carousel"),o=e?.querySelector(".prev"),c=e?.querySelector(".next");o?.addEventListener("click",(()=>{r?.scrollBy({left:-300,behavior:"smooth"})})),c?.addEventListener("click",(()=>{r?.scrollBy({left:300,behavior:"smooth"})}))}));